const Database = require('better-sqlite3');
const path = require('path');

const db = new Database(path.join(process.cwd(), 'database.sqlite'));

function addMoreKeywords(slug, additionalKeywords) {
  const current = db
    .prepare('SELECT keywords FROM page_keywords WHERE page_slug = ?')
    .get(slug);
  if (current) {
    const newKeywords = current.keywords + ', ' + additionalKeywords;
    db.prepare(
      "UPDATE page_keywords SET keywords = ?, updated_at = datetime('now') WHERE page_slug = ?"
    ).run(newKeywords, slug);
    const count = newKeywords.split(',').length;
    console.log(`âœ… ${slug} - ${count} ÙƒÙ„Ù…Ø©`);
  }
}

